local MyLayer = class("MyLayer", function()    local layer = cc.Layer:create()    return layerend)function MyLayer.create()       --定义create函数  local map=MyLayer.new()   return mapendfunction MyLayer:ctor()  local visibleSize = cc.Director:getInstance():getVisibleSize()  --print(visibleSize.height)  local map1 = cc.Sprite:create("map.jpg")  local picsize={}  picsize.width=800   picsize.height=2048  --print(string.format("picsize%d",picsize.height))      map1:setPosition(visibleSize.width/2,visibleSize.height/2)  self:addChild(map1)  local map2 = cc.Sprite:create("map.jpg")  map2:setPosition(visibleSize.width/2,picsize.height+visibleSize.height/2)   self:addChild(map2)   function tick()        local x1,y1 = map1:getPosition()        local x2,y2 = map2:getPosition()             if y1 < -(picsize.height-visibleSize.height/2) then                y1 = picsize.height+visibleSize.height/2                y2 = visibleSize.height/2                                    elseif y2 < -(picsize.height-visibleSize.height/2) then                 y2 = picsize.height+visibleSize.height/2                 y1 = visibleSize.height/2                              else                 y2 = y2 - 1                 y1 = y1 - 1             end             map1:setPositionY(y1)             map2:setPositionY(y2)end cc.Director:getInstance():getScheduler():scheduleScriptFunc(tick, 0, false)endreturn MyLayer